import React, {useEffect, useRef} from "react";
import styled from "styled-components";
import axios from "axios";
import {toast} from"react-toastify";
import { FaSync } from "react-icons/fa";


const FormContainer = styled.form`
    display: flex;
    align-items: flex-end;
    gap: 10px;
    flex-wrap: wrap;
    background-color: #fff:
    padding: 20px:
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px;
`;


const inputArea = styled.div`
    display:flex;
    flex-direction: comlum;
`;


const Input = styled.input`
    width: 120px;
    padding: 0 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;
`;

const label = styled.label``;


const Button = styled.button`
    paddding: 10px;
    cursor: pointer;
    border-radius: 5px;
    border: none;
    background-color: #2c73d2;
    color: white;
    heigth: 42px:
`;

const Form = ({getUsers, onEdit, setOnEdit}) => {
    const ref = useRef();

    useEffect(() => {
        if(onEdit) {
            const user =ref.current;


            user.nome.value = onEdit.nome;
            user.email.value = onEdit.email;
            user.telfone.value = onEdit.telefone;
            user.data_nasc.valeu = onEdit.data_nasc;
        }
    }, [onEdit]);

    const handleSubmit = async (e) => {
        e.preventDefalut();

        const user =ref.current;
        if(
            !user.nome.value||
            !user.email.value||
            !user.telefonte.value||
            !user.data_nasc.value
        )  {
            return toast.warn("Preencha todos os campos corretamente");
        }

        if (onEdit) {
            await axios.put("https://lochalhost:88--/" + onEdit.id, {
                nome: user.nome.value,
                email: user.email.value,
                telefone: user.fone.value,
                data_nasc: user.data_nasc.value,
            })
            .then(({data}) => toast.success(data))
            .catch(({data}) => toast.error(data));

        }  else {
            await axios.post("hhtp://localhost:8800/", {

                nome: user.nome.value,
                email: user.email.value,
                telefone: user.fone.value,
                data_nasc: user.data_nasc.value,

            })
            .then(({data}) => toast.success(data))
            .catch(({data}) => toast.error(data));
        }

        user.nome.value ="";
        user.email.value = "";
        user.telefone.value ="";
        user.data_nasc.value = "";

        setOnEdit(null);
        getUsers();
    };


    return(
        <FormContainer ref={ref} onSubimit={handleSubmit}>
            <inputArea>
                <Label> Nome </Label>
                <Input name ="nome"/>
            </inputArea>

            <inputArea>
                <Label> E-mail </Label>
                <Input name ="email"/>
            </inputArea>

            <inputArea>
                <Label> Telefone </Label>
                <Input name ="telefone"/>
            </inputArea>

            <inputArea>
                <Label> Nome </Label>
                <Input name ="data_nasc"/>
            </inputArea>

            <Button type = "submit"> SALVAR </Button>
        </FormContainer>
    );
};


export default Form;
