import {createGlobalStyle} from "styled-components";

const Global = createGlobalStyle`
    *{
      margin: 0;
      padding: 0;
      font-family: 'poppins', sans-serif;
}


    body {
        width :'00vw;
        heigth: 100vh;
        backgorund-color: #f2f2f2;
    }
`;

export default GlobalStyle;
